DROP TABLE EMLSME_PROFILES;CREATE TABLE EMLSME_PROFILES(  ADDRESS VARCHAR(21) NOT NULL,  USERNAME VARCHAR(64) NOT NULL,  FORWARD VARCHAR(64),  DAILY_LIMIT NUMBER(9));CREATE UNIQUE INDEX EMLSME_IDX_PHONE ON EMLSME_PROFILES(ADDRESS);CREATE UNIQUE INDEX EMLSME_IDX_EMAIL ON EMLSME_PROFILES(USERNAME);DROP TABLE EMLSME_HISTORY;CREATE TABLE EMLSME_HISTORY(  ADDRESS VARCHAR(21) NOT NULL,  MSG_DATE DATE);CREATE INDEX EMLSME_IDX_HISTORY_DATE ON EMLSME_HISTORY(MSG_DATE);CREATE INDEX EMLSME_IDX_HISTORY_ADDR ON EMLSME_HISTORY(ADDRESS);